import React, { useState } from "react";
import { motion } from "framer-motion";
import "sweetalert2/dist/sweetalert2.min.css";
import SweetAlert from "./sweetalert";
import Cover from "./cover";
import axios from "axios";

const App = () => {
  // État pour stocker les valeurs du formulaire
  const [state, setState] = useState({
    age: 40,
    hypertension: 0,
    heart_disease: 0,
    avg_glucose_level: 83.94,
    bmi: 28.89,
  });

  // État pour stocker l'action recommandée
  const [action, setAction] = useState("");

  // Fonction pour envoyer les données au backend et obtenir la prédiction
  const handlePredict = async () => {
    try {
      const response = await axios.post("http://localhost:5000/predict", {
        text: text,
      });
      setSentiment(response.data.sentiment);
    } catch (error) {
      console.error("Erreur lors de la prédiction :", error);
      setError("Erreur lors de la prédiction");
    }
  };

  return (
    <div className="container mx-auto p-4 flex flex-col justify-center items-center text-white h-screen">
      <Cover />
      <h1 className="text-4xl font-bold mb-3 text-center">
        Healthcare Action Predictor
      </h1>
      <p className="text-sm md:text-lg font-normal md:font-medium mb-8 text-center">
        Entrez les informations du patient pour obtenir une recommandation.
      </p>

      {/* Formulaire pour saisir les informations du patient */}
      <div className="space-y-4 w-full max-w-md">
        <div>
          <label className="block mb-2 text-sm font-medium text-white">
            Âge :
          </label>
          <input
            type="number"
            value={state.age}
            onChange={(e) => setState({ ...state, age: e.target.value })}
            className="w-full text-black px-2 py-1 rounded border"
          />
        </div>

        <div>
          <label className="block mb-2 text-sm font-medium text-white">
            Hypertension (0 = Non, 1 = Oui) :
          </label>
          <input
            type="number"
            value={state.hypertension}
            onChange={(e) =>
              setState({ ...state, hypertension: e.target.value })
            }
            className="w-full text-black px-2 py-1 rounded border"
          />
        </div>

        <div>
          <label className="block mb-2 text-sm font-medium text-white">
            Maladie cardiaque (0 = Non, 1 = Oui) :
          </label>
          <input
            type="number"
            value={state.heart_disease}
            onChange={(e) =>
              setState({ ...state, heart_disease: e.target.value })
            }
            className="w-full text-black px-2 py-1 rounded border"
          />
        </div>

        <div>
          <label className="block mb-2 text-sm font-medium text-white">
            Niveau de glucose moyen :
          </label>
          <input
            type="number"
            value={state.avg_glucose_level}
            onChange={(e) =>
              setState({ ...state, avg_glucose_level: e.target.value })
            }
            className="w-full text-black px-2 py-1 rounded border"
          />
        </div>

        <div>
          <label className="block mb-2 text-sm font-medium text-white">
            IMC (Indice de Masse Corporelle) :
          </label>
          <input
            type="number"
            value={state.bmi}
            onChange={(e) => setState({ ...state, bmi: e.target.value })}
            className="w-full text-black px-2 py-1 rounded border"
          />
        </div>
      </div>

      {/* Bouton pour déclencher la prédiction */}
      <motion.button
        whileHover={{ scale: 1.06 }}
        whileTap={{ scale: 0.95 }}
        onClick={handlePredict}
        className="bg-blue-500 text-white px-4 py-2 rounded mt-6 text-base"
      >
        Obtenir la recommandation
      </motion.button>

      {/* Affichage de l'action recommandée */}
      {action && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.6 }}
          className="mt-6 text-xl font-semibold text-green-400"
        >
          Action recommandée : {action}
        </motion.div>
      )}
    </div>
  );
};

export default App;
